create procedure Consolas	@opcion int = 0,	@Codigo_Articulo int = 0,	@Descripcion_articulo varchar(max) = '', 	@Precio_articulo int, 	@imagen image = null,	@nombre_deArticulo varchar(20) = ''		as	if @opcion = 1	begin		insert into Articulo values(@opcion, @Descripcion_articulo, @Precio_articulo, @imagen, @nombre_deArticulo)	end	if @opcion = 2	begin		select * from Articulo	end	if @opcion = 3	begin		delete Articulo where Codigo_Articulo = @Codigo_Articulo	end	if @opcion = 4	begin		update Articulo set Descripcion_articulo = @Descripcion_articulo, Precio_articulo = @Precio_articulo, imagen = @imagen, nombre_deArticulo = @nombre_deArticulo where Codigo_Articulo = @Codigo_Articulo	end
